<?phpnamespace App\AdminModule\Presenters\Forms\Verzie;use Nette\Application\UI\Form;/** * Tovarnicka pre formular na editaciu verzie * Posledna zmena 05.05.2017 *  * @author     Ing. Peter VOJTECH ml. <petak23@gmail.com> * @copyright  Copyright (c) 2012 - 2017 Ing. Peter VOJTECH ml. * @license * @link       http://petak23.echo-msz.eu * @version    1.0.1 */class EditVerzieFormFactory {  /**   * Edit hlavne menu form component factory.   * @return Nette\Application\UI\Form   */    public function create($send_e_mail_news = FALSE)  {    $form = new Form();		$form->addProtection();    $form->addGroup();    $form->addHidden("id");    $form->addHidden("id_user_profiles");		$form->addText('cislo', 'Číslo verzie:', 50, 80)         ->setAttribute('autofocus', 'autofocus')				 ->addRule(Form::FILLED, 'Číslo verzie musí byť zadané!');		$form->addText('subory', 'Zmenené súbory:', 50, 80);    if ($send_e_mail_news) {      $form->addCheckbox('posli_news', ' Posielatie NEWS o tejto aktualite');    } else {      $form->addHidden("posli_news", FALSE);    }		$form->addTextArea('text', 'Popis zmien:', 60, 15)->setAttribute('class', 'jquery_ckeditor');		$form->addSubmit('uloz', 'Ulož')         ->setAttribute('class', 'btn btn-success');    $form->addSubmit('cancel', 'Cancel')->setAttribute('class', 'btn btn-default')         ->setValidationScope(FALSE);		return $form;	}}