<?phpnamespace App\FrontModule\Presenters\Forms\User;use Nette\Application\UI\Form;use Nette\Security;/** * Prihlasovaci formular * Posledna zmena 16.05.2016 *  * @author     Ing. Peter VOJTECH ml. <petak23@gmail.com> * @copyright  Copyright (c) 2012 - 2016 Ing. Peter VOJTECH ml. * @license * @link       http://petak23.echo-msz.eu * @version    1.0.0 */class SignInFormFactory {  /** @var Security\User */  protected $user;  /** @param Security\User $user   */  public function __construct(Security\User $user) {    $this->user = $user;	}    /**   * Prihlasovaci formular   * @param array $texts   * @return Nette\Application\UI\Form */  public function create($texts)  {    $form = new Form();		$form->addProtection();		$form->addText('username', $texts['base_SignInForm_username'], 40, 20)				 ->setRequired($texts['base_SignInForm_username_req']);		$form->addPassword('password', $texts['base_SignInForm_password'], 40)				 ->setRequired($texts['base_SignInForm_password_req']);		$form->addCheckbox('remember', $texts['base_SignInForm_remember']);    $form->addSubmit('login', $texts['base_SignInForm_login'])         ->setAttribute('class', 'btn btn-success')         ->onClick[] = [$this, 'signInFormSubmitted'];		return $form;	}    /**    * Overenie po prihlaseni   * @param Nette\Forms\Controls\SubmitButton $button Data formulara */	public function signInFormSubmitted($button) {    $values = $button->getForm()->getValues();    try {			if ($values->remember) {				$this->user->setExpiration('+ 14 days', FALSE);			} else {				$this->user->setExpiration('+ 30 minutes', TRUE);			}			$this->user->login($values->username, $values->password);		} catch (Security\AuthenticationException $e) {      $button->addError($e->getMessage());    }	}}