<?phpnamespace DbTable;use Nette\Database;use Nette\Utils\Strings;/** * Model, ktory sa stara o tabulku pokladnicka * Posledna zmena 25.05.2016 *  * @author     Ing. Peter VOJTECH ml. <petak23@gmail.com> * @copyright  Copyright (c) 2012 - 2016 Ing. Peter VOJTECH ml. * @license * @link       http://petak23.echo-msz.eu * @version    1.0.0 */class Pokladnicka extends Table {  /** @var string */  protected $tableName = 'pokladnicka';  /**   * Vrati vsetky polozky v poradi od najnovsej   * @return \Nette\Database\Table\Selection */  public function pokladnicka() {    return $this->getTable()->order('created ASC');  }  /**   *    * @param Nette\Utils\ArrayHash $values   * @return Nette\Database\Table\ActiveRow|FALSE   * @throws Nette\Database\DriverException   */  public function ulozPokladnicka($values) {    try {      $id = ($values->id) ? $values->id : 0;      unset($values->id);      if (isset($values->suma)){        $values->suma = Strings::replace($values->suma, '~,~', '.');      }      return $this->uloz($values, $id);    } catch (Exception $e) {      throw new Database\DriverException('Chyba ulozenia: '.$e->getMessage());    }  }}